import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Buttonshop } from './Buttonshop';
import axios from 'axios';
import { useEffect } from 'react';
import './Promotionshop.css';

import Promotiondetails from './Promotiondetails';


function Promotionshop() {

    //-----------------------------------------------

    const [shoplist, setShoplist] = useState([]);

    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    };

    const getpromotion = (shopname) => {
        axios.post("/selectpromotionshop", {
            shop_name: shopname
        })
            .then((response) => {
                //ใส่ modal
                console.log(response.data);
            });

        //window.location.replace('/promotiondetails');
    };

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {

            }
            if (response.data.loggedIn == false || response.data.user[0].role != 'shop') {
                return (window.location.replace('/'));
            }
        });

        axios.get("/getshoplistpromotion").then((response) => {
            if (response) {
                setShoplist(response.data);
            }
        });

    }, []);

    return (
        <>
            <nav class="navbar navbar-light bg-light justify-content-between">
                <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i></Link>
            </nav>

            <div className='add-promotion'>
                <Link to="/addpromotion" className="add">
                    <i class='fas fa-plus'></i> เพิ่มโปรโมชั่น
                </Link><br />
                <div className="row mb50" onClick={() => { getpromotion(val.shop_name) }}>
                    <div className="col-xl-3 col-lg-3 col-md-4 col-sm-12">
                        <div className="speakers xs-mb30">
                            <div className="spk-info">
                                <p>ร้านค้าของฉัน</p>
                            </div>
                        </div>
                    </div>
                </div>
                <br />
                <div className="whos-speaking-area speakers pad100">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-12">
                                <div className="section-title text-center">
                                    <div className="title-text mb50">
                                        <h2>โปรโมชั่นร้านค้า</h2>
                                        <input type="text" placeholder="search" /> <i className="fas fa-search"></i>
                                    </div>
                                </div>
                            </div>

                        </div>
                        {shoplist.map((val) => {
                            return (
                                <div className="row mb50" onClick={() => { getpromotion(val.shop_name) }}>
                                    <div className="col-xl-3 col-lg-3 col-md-4 col-sm-12">
                                        <div className="speakers xs-mb30">
                                            <div className="spk-info">
                                                <p>{val.shop_name}</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )
                        })}


                    </div>

                </div>

            </div>


        </>

    );
}
export default Promotionshop;
