import React from "react";
import { useState } from "react";
import { useHistory } from "react-router";
import "./Forgotpwd.css";

function Forgotpwd() {
  const history = useHistory();
  const [email, setemail] = useState("");
  const forgotpassword = () => {
    //asfafsfa;fkljkl;fasjf;klasfl
  };
  const backToLogin = () => {
    history.push("/login");
  };
  return (
    <div>
      <form onSubmit={forgotpassword}>
        <h1 className="h1-forgotpwd">Forgot Password</h1>
        <label className="label-forgotpwd">Email</label>
        <input
          className="input-forgotpwd"
          type="email"
          placeholder="sample@hotmail.com"
          onChange={(e) => {
            setemail(e.target.value);
          }}
        />
        {email}
        <button
          onClick={backToLogin}
          className="btn btn-danger forgotpwd-btn-cancel"
        >
          ยกเลิก
        </button>
        <button type="submit" className="btn btn-primary forgotpwd-btn-submit">
          ยืนยัน
        </button>
      </form>
    </div>
  );
}

export default Forgotpwd;
