import React from "react";
import { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import './login_and_register.css';
import { Redirect } from "react-router";
import { Link } from "react-router-dom";
import './Login.css';


export default function Login() {



  //----------------------------------------------------------

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [loginStatus, setLoginSatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;

  //login
  const login = () => {
    axios.post("/login", {
      email: email,
      password: password,
    })
      .then((response) => {
        if (response.data.message) {
          setLoginSatus(response.data.message);
        } else {
          window.location.replace('/');
        }
      });
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn == "true") {
    return (
      <Redirect to="/" />
    );
  }
  return (
    <>
      <div className="navbar navbar-default navbar-static-top">
        <div className="container bootstrap snippets bootdey">
          <div className="navbar-header">

            <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i><i className="fas fa-braille" ></i>T N C</Link>

          </div>

        </div>
      </div>


      <section class="vh-100 gradient-custom">
        <div class="container py-5 h-100">
          <div class="row d-flex justify-content-center align-items-center h-100">
            <div class="col-12 col-md-8 col-lg-6 col-xl-5">
              <div class="card bg-dark text-white" style="border-radius: 1rem;">
                <div class="card-body p-5 text-center">

                  <div class="mb-md-5 mt-md-4 pb-5">

                    <h2 class="fw-bold mb-2 text-uppercase">Login</h2>
                    <p class="text-white-50 mb-5">Please enter your login and password!</p>

                    <div class="form-outline form-white mb-4">
                      <input type="email" id="typeEmailX" class="form-control form-control-lg" />
                      <label class="form-label" for="typeEmailX">Email</label>
                    </div>

                    <div class="form-outline form-white mb-4">
                      <input type="password" id="typePasswordX" class="form-control form-control-lg" />
                      <label class="form-label" for="typePasswordX">Password</label>
                    </div>

                    <p class="small mb-5 pb-lg-2"><a class="text-white-50" href="#!">Forgot password?</a></p>

                    <button class="btn btn-outline-light btn-lg px-5" type="submit">Login</button>

                    <div class="d-flex justify-content-center text-center mt-4 pt-1">
                      <a href="#!" class="text-white"><i class="fab fa-facebook-f fa-lg"></i></a>
                      <a href="#!" class="text-white"><i class="fab fa-twitter fa-lg mx-4 px-2"></i></a>
                      <a href="#!" class="text-white"><i class="fab fa-google fa-lg"></i></a>
                    </div>

                  </div>

                  <div>
                    <p class="mb-0">Don't have an account? <a href="#!" class="text-white-50 fw-bold">Sign Up</a></p>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <div className="login-wrap">
        <div className="login-html">
          <input id="tab-1" type="radio" name="tab" className="sign-in" checked /><label for="tab-1" className="tab" >เข้าสู่ระบบ</label>
          <input id="tab-2" type="radio" name="tab" className="sign-up" /><label for="tab-2" className="tab" ></label>
          <div className="login-form">
            <div className="sign-in-htm">
              <div className="group">
                <label for="user" className="label">Email</label>
                <input className="input" type="text"
                  placeholder="Email"
                  onChange={(e) => {
                    setEmail(e.target.value);
                  }}
                  required />
              </div>
              <div className="group">
                <label for="pass" className="label">Password</label>
                <input className="input" type="password"
                  placeholder="Password"
                  onChange={(e) => {
                    setPassword(e.target.value);
                  }}
                  required />
              </div>

              <div className="notification">
                <p className="notify">{loginStatus}</p>
              </div>

              <div className="hr"></div>
              <div className="foot-lnk">
                <div className="group">
                  <p>ยังไม่ได้เป็นสมาชิค?</p>
                  <Link to='/registration'>สมัครสมาชิก</Link><br /><br />
                  <input onClick={login} type="submit" className="button" value="LOGIN" />
                </div>
              </div>
            </div>

          </div>
        </div>
      </div>

    </>
  );
}
