import React, { useState } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { useEffect } from 'react'
import axios from 'axios'
import Modal from 'react-modal'
import { Loader } from 'semantic-ui-react'
import './Transferpoint.css'
import { motion } from 'framer-motion'

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
}

export default function Transferpoint() {
  let subtitle
  const [modalIsOpenCheck, setIsOpenCheck] = useState(false)
  const [modalIsOpenConfirm, setIsOpenConfirm] = useState(false)
  const [destname, setDestName] = useState('')

  const [active, setactive] = useState(false) //loader
  //--------------------------------------------------------------
  function backtoHome() {
    history.push('/')
    history.go()
  }
  function openModalCheck() {
    axios
      .post('/transfer/check', {
        phone: destPhone,
      })
      .then((response) => {
        if (response.data.message) {
          setStatus(response.data.message)
        } else if (response.data) {
          setDestName(response.data[0].name)
          setIsOpenCheck(true)
        }
      })
  }

  function openModalConfirm() {
    setactive(true)
    const timer = setTimeout(() => {
      axios
        .put('/memberPointTransfer', {
          id: id,
          phone: phone,
          destphone: destPhone,
          amount: amount,
        })
        .then((response) => {
          if (response.data.message) {
            alert(response.data.message)
          } else if (response.data.loggedIn === true) {
            setIsOpenConfirm(true)
            setPoint(response.data.user[0].point)
          }
        })
      setactive(false)
    }, 4000)
    return () => clearTimeout(timer)
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = 'black'
  }

  function closeModal() {
    setIsOpenCheck(false)
    setIsOpenConfirm(false)
  }
  //-------------------------------------------------------
  axios.defaults.withCredentials = true

  const history = useHistory()
  const [id, setId] = useState()
  const [point, setPoint] = useState()
  const [name, setName] = useState('')
  const [phone, setPhone] = useState('')
  const [destPhone, setdestPhone] = useState('')
  const [amount, setamount] = useState()
  const [status, setStatus] = useState('')

  const checkinput = () => {
    if (!destPhone) {
      setStatus('เบอร์ปลายทางไม่ถูกต้อง')
    } else if (phone === destPhone) {
      setStatus('เบอร์ปลายทางไม่ถูกต้อง')
    } else if (!amount) {
