import React from 'react';
import { Link } from 'react-router-dom';
import { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import moment from "moment";
import './Transaction.css';

import { PullToRefresh } from "react-js-pull-to-refresh";



function Transaction() {

    //------------------------------------------------------------
    axios.defaults.withCredentials = true;

    const [history, setHistory] = useState([]);
    const [phone, setPhone] = useState("");

    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {
                setPhone(response.data.user[0].phone);
                axios.get("/transaction").then((response) => {
                    if (response) {
                        setHistory(response.data);
                    }
                });
            }
            if (response.data.loggedIn == false || response.data.user[0].role != 'member') {
                return (window.location.replace('/'));
            }
        });

    }, []);

    return (
        <>
            <nav class="navbar navbar-light bg-light justify-content-between">
                <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i></Link>
            </nav>
            <PullToRefresh
                pullDownThreshold={200}
                triggerHeight={50}
                backgroundColor='white'
                startInvisible={true}
            >
                <div style={{ height: '150vh', textAlign: 'center' }}>
                    <div>PullToRefresh</div>
                </div>
            </PullToRefresh>
            <div className="history">
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">วันที่โอน</th>
                            <th scope="col">ผู้โอน</th>
                            <th scope="col">ผู้รับโอน</th>
                            <th scope="col">จำนวน</th>
                        </tr>
                    </thead>
                    {history.map((val) => {
                        return (
                            <tbody>
                                <tr>
                                    <th scope="row">{moment({ val }).format("DD/MM/YYYY")}</th>
                                    <td key={val.id}>{val.transferor}</td>
                                    <td>{val.recipient}</td>
                                    {val.transferor == phone ? <td><p className="amountminus" >- {val.amount}</p></td>
                                        : <td><p className="amountplus">+ {val.amount}</p></td>}
                                </tr>

                            </tbody>
                        )
                    })}
                </table>

            </div>

        </ >

    );
}
export default Transaction;
