import React from 'react';
import { BrowserRouter as Router, Redirect, Route, Switch } from "react-router-dom";
import "./App.css";
import axios from 'axios';
import { useEffect } from 'react';
import { useState } from 'react';

import Registeration from './pages/Registeration';
import Login from './pages/Login';
import Main from './pages/Main';
import ProtectedRoute from './components/ProtectedRoute';

import Transferpoint from './components/Transferpoint';
import Transaction from './components/Transaction';
import Promotion from './components/Promotion';
import Memberprofile from './components/Memberprofile';

import Transferpointshop from './components/Transferpointshop';
import Calculatepointshop from './components/Calculatepointshop';
import Transactionshop from './components/Transactionshop';
import Promotionshop from './components/Promotionshop';
import Topuppointshop from './components/Topuppointshop';
import Shopprofile from './components/Shopprofile';

axios.defaults.baseURL = "http://localhost:3001";


function App() {

  axios.defaults.withCredentials = true;

  const [Auth, setAuth] = useState(false);
  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setAuth(response.data.loggedIn);
      }
      if (response.data.loggedIn == false) {
        setAuth(response.data.loggedIn);
      }
    });
  }, []);
  return (
    <Switch>
      <Route exact path="/" component={Main} />
      <Route path="/registration" exact render={(props) => <Registeration />} />
      <Route path="/login" exact render={(props) => <Login />} />

      <ProtectedRoute path="/transfer-point" Auth={Auth} component={Transferpoint} />
      <ProtectedRoute path="/transaction" Auth={Auth} component={Transaction} />
      <ProtectedRoute path="/promotion" Auth={Auth} component={Promotion} />
      <ProtectedRoute path="/memberprofile" Auth={Auth} component={Memberprofile} />

      <ProtectedRoute path="/transfer-point-shop" Auth={Auth} component={Transferpointshop} />
      <ProtectedRoute path="/calculate-point-shop" Auth={Auth} component={Calculatepointshop} />
      <ProtectedRoute path="/transaction-shop" Auth={Auth} component={Transactionshop} />
      <ProtectedRoute path="/promotion-shop" Auth={Auth} component={Promotionshop} />
      <ProtectedRoute path="/top-up-point-shop" Auth={Auth} component={Topuppointshop} />
      <ProtectedRoute path="/shopprofile" Auth={Auth} component={Shopprofile} />

    </Switch>
  );
}

export default App;
