import axios from "axios";
import React from "react";
import { useState } from "react";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import "./Forgotpwd.css";
import { motion } from "framer-motion";

function Forgotpwd() {
  const history = useHistory();
  const [email, setemail] = useState("");
  const [status, setStatus] = useState("");

  const forgotpassword = (e) => {
    e.preventDefault();
    axios.post("/forgotpassword" + email).then((response) => {
      if (response.data.email) {
        history.push("/changepwd/" + response.data.email);
      } else if (response.data.message) {
        setStatus(response.data.message);
      }
    });
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */
  return (
    <div>
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/login">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>
      <motion.div initial="out" animate="in" exit="out" variants={pageTransition}>

        <form onSubmit={forgotpassword}>
          <h1 className="h1-forgotpwdd">ลืมรหัสผ่าน</h1>
          <label className="label-forgotpwd" style={{ color: "red" }}>Email ที่ลืมรหัสผ่าน</label>
          {/* <label className="label-forgotpwd">Email</label> */}
          <input
            type="email"
            className="input-forgotpwd"
            placeholder="sample@hotmail.com"
            onChange={(e) => {
              setemail(e.target.value);
            }}
            required
          />
          <p style={{ color: "red" }}>{status}</p>
          <button className="btn btn-primary forgotpwd-btn-submit">ยืนยัน</button>
          <a href="/login" className="cancel-forgotpwd">
            ยกเลิก
          </a>
        </form>
      </motion.div>
    </div>
  );
}

export default Forgotpwd;
