import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useEffect } from "react";
import Modal from "react-modal";
import axios from "axios";
import { AnimatePresence, motion } from "framer-motion";

import "./Memberprofile.css";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Memberprofile() {
  //----------------------------------------------------------
  let subtitle;
  const [modalIsOpenid, setIsOpenid] = React.useState(false);
  const [modalIsOpenname, setIsOpenname] = React.useState(false);
  const [modalIsOpenphone, setIsOpenphone] = React.useState(false);
  const [modalIsOpenemail, setIsOpenemail] = React.useState(false);
  const [modalIsOpenPassword, setIsOpenPassword] = React.useState(false);

  function openModalid() {
    setIsOpenid(true);
  }

  function openModalname() {
    setIsOpenname(true);
  }

  function openModalphone() {
    setIsOpenphone(true);
  }

  function openModalemail() {
    setIsOpenemail(true);
  }

  function openModalpassword() {
    setIsOpenPassword(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "#000";
  }

  function closeModal() {
    setIsOpenid(false);
    setIsOpenname(false);
    setIsOpenphone(false);
    setIsOpenemail(false);
    setIsOpenPassword(false);
  }

  const history = useHistory();
  const [id, setId] = useState();
  const [national_id, setNationalId] = useState("");
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [point, setPoint] = useState();
  const [role, setRole] = useState("");
  const [statusID, setStatusID] = useState("");
  const [statusName, setStatusName] = useState("");
  const [statusPhone, setStatusPhone] = useState("");
  const [statusEmail, setStatusEmail] = useState("");
  const [statusPassword, setStatusPassword] = useState("");

  const [neweNational_id, setnewNationalId] = useState("");
  const [newname, setnewName] = useState("");
  const [newphone, setnewPhone] = useState("");
  const [newemail, setnewEmail] = useState("");
  const [oldPassword, setoldPassword] = useState("");
  const [newePassword, setnewPassword] = useState("");
  const [newConfirmPassword, setnewConfirmPassword] = useState("");

  axios.defaults.withCredentials = true;

  const refresh = () => {
    axios.get("/member");
    history.push("/memberprofile");
  };

  const idUpdate = () => {
    if (!neweNational_id) {
      setStatusID("เลขบัตรประจำตัวประชาชนไม่ถูกต้อง");
    } else {
      axios
        .put("/updatememberId", { national_id: neweNational_id, id: id })
        .then((response) => {
          if (response.data.message) {
            setStatusID(response.data.message);
          } else {
            alert("บันทึกสำเร็จ");
            setNationalId(response.data.user[0].national_id);
            history.go();
          }
        });
    }
  };

  const nameUpdate = () => {
    if (!newname) {
      setStatusName("ชื่อไม่ถูกต้อง");
    } else {
      axios
        .put("/updatememberName", { name: newname, id: id })
        .then((response) => {
          alert("บันทึกสำเร็จ");
          return setName(response.data.user[0].name);
        });
    }
  };
  const phoneUpdate = () => {
    if (!newphone) {
      setStatusPhone("เบอร์โทรไม่ถูกต้อง");
    } else {
      axios
        .put("/updatememberPhone", { phone: newphone, id: id })
        .then((response) => {
          if (response.data.message) {
            setStatusPhone(response.data.message);
          } else {
            alert("บันทึกสำเร็จ");
            setPhone(response.data.user[0].phone);
            history.go();
          }
        });
    }
  };

  const emailUpdate = () => {
    if (!newemail) {
      setStatusEmail("อีเมลไม่ถูกต้อง");
    } else {
      axios
        .put("/updatememberEmail", { email: newemail, id: id })
        .then((response) => {
          if (response.data.message) {
            setStatusEmail(response.data.message);
          } else {
            alert("บันทึกสำเร็จ");
            setEmail(response.data.user[0].email);
            history.go();
          }
        });
    }
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn === true) {
        setId(response.data.user[0].id);
        setNationalId(response.data.user[0].national_id);
        setName(response.data.user[0].name);
        setPhone(response.data.user[0].phone);
        setEmail(response.data.user[0].email);
        setPoint(response.data.user[0].point);
        setRole(response.data.user[0].role);
      }
      if (
        response.data.loggedIn === false ||
        response.data.user[0].role !== "member"
      ) {
        return history.push("/login");
      }
    });
  }, []);
  return (
    <div>
      <nav class="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <div className="memberprofile">
        <Modal
          isOpen={modalIsOpenid}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2
            className="h2h2h2memberprofile"
            ref={(_subtitle) => (subtitle = _subtitle)}
          >
            เลขบัตรประจำตัวประชาชน
          </h2>
          <div className="in-buttonmemberprofile">
            <input
              className="inputmemberprofile"
              type="text"
              placeholder="id"
              onChange={(e) => {
                setnewNationalId(e.target.value);
              }}
            />
            <p style={{ color: "#af0527" }}>{statusID}</p>
            <div>
              <button className="buttoncalculate-modal1" onClick={idUpdate}>
                แก้ไข
              </button>
              <button className="buttoncalculate-modal2" onClick={closeModal}>
                ยกเลิก
              </button>
            </div>
          </div>
        </Modal>
      </div>

      <div className="memberprofile">
        <Modal
          isOpen={modalIsOpenname}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2
            className="h2h2h2memberprofile"
            ref={(_subtitle) => (subtitle = _subtitle)}
          >
            ชื่อ-สกุล
          </h2>
          <div className="in-buttonmemberprofile">
            <input
              className="inputmemberprofile"
              type="text"
              placeholder="name"
              onChange={(e) => {
                setnewName(e.target.value);
              }}
            />
            <p style={{ color: "#af0527" }}>{statusName}</p>

            <div>
              <button className="buttoncalculate-modal1" onClick={nameUpdate}>
                แก้ไข
              </button>
              <button className="buttoncalculate-modal2" onClick={closeModal}>
                ยกเลิก
              </button>
            </div>
          </div>
        </Modal>
      </div>

      <div className="memberprofile">
        <Modal
          isOpen={modalIsOpenphone}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2
            className="h2h2h2memberprofile"
            ref={(_subtitle) => (subtitle = _subtitle)}
          >
            เบอร์โทรศัพท์
          </h2>
          <div className="in-buttonmemberprofile">
            <input
              className="inputmemberprofile"
              type="text"
              placeholder="phone"
              onChange={(e) => {
                setnewPhone(e.target.value);
              }}
            />
            <p style={{ color: "#af0527" }}>{statusPhone}</p>

            <div>
              <button className="buttoncalculate-modal1" onClick={phoneUpdate}>
                แก้ไข
              </button>
              <button className="buttoncalculate-modal2" onClick={closeModal}>
                ยกเลิก
              </button>
            </div>
          </div>
        </Modal>
      </div>

      <div className="memberprofile">
        <Modal
          isOpen={modalIsOpenemail}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2
            className="h2h2h2memberprofile"
            ref={(_subtitle) => (subtitle = _subtitle)}
          >
            อีเมล์
          </h2>
          <form className="in-buttonmemberprofile" onSubmit={emailUpdate}>
            <input
              className="inputmemberprofile"
              type="email"
              placeholder="email"
              onChange={(e) => {
                setnewEmail(e.target.value);
              }}
            />
            <p style={{ color: "#af0527" }}>{statusEmail}</p>

            <div>
              <button className="buttoncalculate-modal1" onClick={emailUpdate}>
                แก้ไข
              </button>
              <button className="buttoncalculate-modal2" onClick={closeModal}>
                ยกเลิก
              </button>
            </div>
          </form>
        </Modal>
      </div>

      <div className="memberprofile">
        <Modal
          isOpen={modalIsOpenPassword}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2
            className="h2h2h2memberprofile"
            ref={(_subtitle) => (subtitle = _subtitle)}
          >
            เปลี่ยนรหัสผ่าน
          </h2>
          <form className="in-buttonmemberprofile" onSubmit={emailUpdate}>
            <input
              className="inputmemberprofile"
              type="password"
              placeholder="รหัสผ่านเก่า"
              onChange={(e) => {
                setoldPassword(e.target.value);
              }}
            />
            <input
              className="inputmemberprofile"
              type="password"
              placeholder="รหัสผ่านใหม่"
              onChange={(e) => {
                setnewPassword(e.target.value);
              }}
            />
            <input
              className="inputmemberprofile"
              type="password"
              placeholder="ยืนยันรหัสผ่านใหม่"
              onChange={(e) => {
                setnewConfirmPassword(e.target.value);
              }}
            />
            <p style={{ color: "#af0527" }}>{statusPassword}</p>

            <div>
              <button className="buttoncalculate-modal1" onClick={emailUpdate}>
                แก้ไข
              </button>
              <button className="buttoncalculate-modal2" onClick={closeModal}>
                ยกเลิก
              </button>
            </div>
          </form>
        </Modal>
      </div>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <div className="container bootstrap snippets bootdey">
          <div className="panel-body inf-content">
            <div className="row">
              <div className="col-md-4">
                <img
                  alt=""
                  title=""
                  className="img-circle img-thumbnail isTooltip"
                  src="images/user.png"
                  data-original-title="Usuario"
                />
                <ul title="Ratings" className="list-inline ratings text-center">
                  <li>
                    <a href="#">
                      <span className="glyphicon glyphicon-star"></span>
                    </a>
                  </li>
                  <li>
                    <a href="#">
                      <span className="glyphicon glyphicon-star"></span>
                    </a>
                  </li>
                  <li>
                    <a href="#">
                      <span className="glyphicon glyphicon-star"></span>
                    </a>
                  </li>
                  <li>
                    <a href="#">
                      <span className="glyphicon glyphicon-star"></span>
                    </a>
                  </li>
                  <li>
                    <a href="#">
                      <span className="glyphicon glyphicon-star"></span>
                    </a>
                  </li>
                </ul>
              </div>
              <div className="col-md-6">
                <strong>ข้อมูลส่วนตัว</strong>
                <br />
                <div className="table-responsive">
                  <table className="table table-user-information">
                    <tbody>
                      <tr>
                        <td>
                          <strong>
                            <span className="glyphicon glyphicon-asterisk text-primary"></span>
                            เลขบัตรประจำตัวประชาชน
                          </strong>
                        </td>
                        <td className="text-primary">
                          {national_id}{" "}
                          <i onClick={openModalid} className="fas fa-pen"></i>
                        </td>
                      </tr>
                      <tr>
                        <td>
                          <strong>
                            <span className="glyphicon glyphicon-user  text-primary"></span>
                            ชื่อ-สกุล
                          </strong>
                        </td>
                        <td className="text-primary">
                          {name}{" "}
                          <i onClick={openModalname} className="fas fa-pen"></i>
                        </td>
                      </tr>

                      <tr>
                        <td>
                          <strong>
                            <span className="glyphicon glyphicon-bookmark text-primary"></span>
                            เบอร์โทรศัพท์
                          </strong>
                        </td>
                        <td className="text-primary">
                          {phone}{" "}
                          <i
                            onClick={openModalphone}
                            className="fas fa-pen"
                          ></i>
                        </td>
                      </tr>

                      <tr>
                        <td>
                          <strong>
                            <span className="glyphicon glyphicon-envelope text-primary"></span>
                            อีเมล์
                          </strong>
                        </td>
                        <td className="text-primary">
                          {email}{" "}
                          <i
                            onClick={openModalemail}
                            className="fas fa-pen"
                          ></i>
                        </td>
                      </tr>

                      <tr>
                        <td>
                          <strong>
                            <span className="glyphicon glyphicon-envelope text-primary"></span>
                            คะแนน
                          </strong>
                        </td>
                        <td className="text-primary">{point}</td>
                      </tr>

                      <tr>
                        <td>
                          <strong>
                            <span className="glyphicon glyphicon-eye-open text-primary"></span>
                            รหัสผ่าน
                          </strong>
                        </td>
                        <td className="text-primary">
                          เปลี่ยนรหัสผ่าน
                          <i
                            onClick={openModalpassword}
                            className="fas fa-pen"
                          ></i>
                        </td>
                      </tr>

                      <tr>
                        <td>
                          <strong>
                            <span className="glyphicon glyphicon-eye-open text-primary"></span>
                            สถานะ
                          </strong>
                        </td>
                        <td className="text-primary">{role}</td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
}
export default Memberprofile;
