import React from "react";
import { useEffect } from 'react';
import axios from 'axios';
import {useState} from 'react';
import "../App.css";
import { Redirect } from "react-router";
import Home from "../components/Home";
import Admin from "../components/Admin";
import Member from '../components/Member';

export default function Registeration() {
  const [nameReg, setNameReg] = useState("");
  const [phoneReg,setPhoneReg] = useState("");
  const [emailReg,setEmailReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  const [typeReg,setTypeReg] = useState("");

  const [email,setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [loginStatus, setLoginStatus] = useState("");
  const [isLoggedIn,setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;
  //register
  const memberregister = () => {
    axios
      .post("http://localhost:3001/memberregister", {
        name: nameReg,
        phone: phoneReg,
        email: emailReg,
        password: passwordReg,
      })
      .then((response) => {
        console.log(response);
      });
  };
  const shopregister = () => {
    axios
      .post("http://localhost:3001/shopregister", {
        name: nameReg,
        phone: phoneReg,
        email: emailReg,
        password: passwordReg,
        type: typeReg
      })
      .then((response) => {
        console.log(response);
      });
  };
  //login
const login = ()=> {
    axios
      .post("http://localhost:3001/login", {
        email: email,
        password: password,
      })
      .then((response) => {
        if (response.data.message) {
          setLoginStatus(response.data.message);
          console.log(response);
        } else {
          setLoginStatus(response.data[0].name);
          window.location.replace('/');
        }
      });
  };
  useEffect(() => {
    axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn == true) {
        setLoginStatus(response.data.user[0].username);
        setIsLoggedIn("true");
      }
    });
  }, []);

  if(isLoggedIn == "true"){
    return (
      <Redirect to ="/" />
    );
  }
  return (
    <div className="App">
      <div className="registerarion">
        <h1>Member Registeration</h1>
        <label>Name-Surname</label>
        <input
          type="text"
          onChange={(e) => {
            setNameReg(e.target.value);
          }}
        />

        <label>Phone</label>
        <input
          type="text"
          onChange={(e) => {
            setPhoneReg(e.target.value);
          }}
        />

        <label>Email</label>
        <input
          type="text"
          onChange={(e) => {
            setEmailReg(e.target.value);
          }}
        />

        <label>Password</label>
        <input
          type="text"
          onChange={(e) => {
            setPasswordReg(e.target.value);
          }}
        />
        <button onClick={memberregister}>Register</button>

        <h1>Member Registeration</h1>
        <label>Name-Surname</label>
        <input
          type="text"
          onChange={(e) => {
            setNameReg(e.target.value);
          }}
        />

        <label>Phone</label>
        <input
          type="text"
          onChange={(e) => {
            setPhoneReg(e.target.value);
          }}
        />

        <label>Email</label>
        <input
          type="text"
          onChange={(e) => {
            setEmailReg(e.target.value);
          }}
        />

        <label>Password</label>
        <input
          type="text"
          onChange={(e) => {
            setPasswordReg(e.target.value);
          }}
        />

        <label>shop type</label>
        <input
          type="text"
          onChange={(e) => {
            setTypeReg(e.target.value);
          }}
        />
        <button onClick={shopregister}>Register</button>
      </div>



      <div className="login">
        <h1>Login</h1>
        <label>Email</label>
        <input
          type="text"
          placeholder="Username..."
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />

        <label>Password</label>
        <input
          type="password"
          placeholder="Password..."
          onChange={(e) => {
            setPassword(e.target.value);
          }}
        />

        <button onClick={login}>Login</button>
        <h1>{loginStatus}</h1>
      </div>
    </div>
  );
}
