import React,{useState} from 'react';
import { Router } from 'react-router-dom';
import{Link} from 'react-router-dom';

import './Memberprofile.css';


function Memberprofile(){
    const [click,setClick] = useState(false);
    const [button,setButton] = useState(true);

    
    const handleClick= () => setClick(!click);
    const closeMobileMenu =() => setClick(false);
   
    const showButton=() => {
        if(window.innerWidth <= 960){
            setButton(false)
        }else{
            setButton(true)
        }
    };

    window.addEventListener('resize',showButton);

    const [id,setId] = useState();
    const [name, setName] = useState("");
    const [phone, setPhone] = useState("");
    const [email, setEmail] = useState("");
    const [point, setPoint] = useState();

    const [newname, setnewName] = useState("");
    const [newphone, setnewPhone] = useState("");

    axios.defaults.withCredentials = true;
    const logout = () => {
      axios.get("/logout");
      window.location.replace('/');
    }
    const refresh = () => {
      axios.get("/member");
      window.location.replace('/');
    }
    const nameUpdate = () => {
      axios.put("/updatememberName",{name: newname,id: id});
      axios.get("/member");
      window.location.replace('/');
    }
    const phoneUpdate = () => {
      axios.put("/updatememberPhone",{phone: newphone,id: id}).then((response) => {
        if(response.data.message){
        return setStatusPhone(response.data.message);
        }
        return window.location.replace('/');
      });
    }
    const  pointTransfer = () => {
      axios.put("/pointTransfer",{id : id, phone : destPhone, amount : amount});
    }

    useEffect(() => {
        axios.get("http://localhost:3001/login").then((response) => {
          if (response.data.loggedIn == true) {
              setId(response.data.user[0].id);
              setName(response.data.user[0].name);
              setPhone(response.data.user[0].phone);
              setEmail(response.data.user[0].email);
              setPoint(response.data.user[0].point);
          }
        });
      }, []);

    return (
       <>
        <nav className="navbar">
           <div className="navbar-container">
               <Link to="/" className="navbar-logo">
                   TNC 
               </Link>
               <div className='menu-icon' onClick={handleClick}>
                   <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />
                  
               </div> 
               

               <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                  
                      <li className='nav-item'>
                       <Link  className='nav-links' onClick={closeMobileMenu}>
                          
                       </Link>
                   </li>

                    <Link to="/ออกจากระบบ" className="navbar-logo">
                     <i class='fas fa-sign-out-alt'></i>
                     </Link>
               </ul>
          
            
           </div>

        </nav>

        <div>
            <h1>member</h1>
            <p>Hi  {name} </p>
            <p>Phone : {phone} <br/>Email : {email} <br/>Point :  {point} <button onClick={refresh}>refresh</button></p>
            <br/>
            <input  type="text" placeholder="name"
              onChange={(e) => 
              {setnewName(e.target.value)}
              }></input>
              <button className="btn" onClick={nameUpdate}>update</button>

            <input  type="text" placeholder="Phone"
              onChange={(e) => 
              {setnewPhone(e.target.value)}
              }></input>
              {statusPhone}
              <button className="btn" onClick={phoneUpdate}>update</button>

            <input  type="text" placeholder="เบอร์โทรผู้รับคะแนน"
              onChange={(e) => 
              {setdestPhone(e.target.value)}
              }></input>
            <input  type="number" placeholder="จำนวนคะแนน"
              onChange={(e) => 
              {setamount(e.target.value)}
              }></input>
            <button className="btn" onClick={pointTransfer}>โอนคะแนน</button>

            <br/><button className="logout" onClick={logout}>Logout</button>
        </div>

       </> 

    );
}
export default Memberprofile;
