import React from 'react';
import { Link } from 'react-router-dom';
import { Buttonmember } from './Buttonmember';
import { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import moment from "moment";
import './Transaction.css';



function Transaction() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);


    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false)
        } else {
            setButton(true)
        }
    };

    window.addEventListener('resize', showButton);
    //------------------------------------------------------------
    axios.defaults.withCredentials = true;

    const [history, setHistory] = useState([]);
    const [phone,setPhone] = useState("");

    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {
                setPhone(response.data.user[0].phone);
                axios.get("/transaction").then((response) => {
                    if (response) {
                        setHistory(response.data);
                    }
                });
            }
        });

    }, []);

    return (
        <div className="transaction">
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo">
                        TNC
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

                    </div>


                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>

                        <li className='nav-item'>
                            <p className='nav-links' onClick={closeMobileMenu}></p>
                        </li>

                        {button && <Buttonmember buttonStyle='btn--outline'>MY PROFILE</Buttonmember>}
                        <p onClick={logout} className="navbar-logo">
                            <i className='fas fa-sign-out-alt'></i>
                        </p>
                    </ul>
                </div>

            </nav>

            <div className="history">
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">วันที่โอน</th>
                            <th scope="col">เวลา</th>
                            <th scope="col">ผู้โอน</th>
                            <th scope="col">ผู้รับโอน</th>
                            <th scope="col">จำนวน</th>
                        </tr>
                    </thead>
                    {history.map((val) => {
                        return (
                            <tbody>
                                <tr>
                                    <th scope="row">{moment({ val }).format("L")}</th>
                                    <th scope="row">{moment({ val }).format("llll")}</th>
                                    <td key = {val.id}>{val.transferor}</td>
                                    <td>{val.recipient}</td>
                                    {val.transferor == phone ? <td>- {val.amount}</td> : <td>+ {val.amount}</td>}
                                </tr>

                            </tbody>
                        )
                    })}
                </table>

            </div>

        </div >

    );
}
export default Transaction;
