import React from "react";
import { Link, useHistory } from "react-router-dom";
import { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
import moment from "moment";
import { Loader } from "semantic-ui-react";
import "./Transaction.css";
import { AnimatePresence, motion } from "framer-motion";

function Transaction() {
  const history = useHistory();
  //------------------------------------------------------------
  axios.defaults.withCredentials = true;

  const [History, setHistory] = useState([]);
  const [phone, setPhone] = useState("");
  const [active, setactive] = useState(true); //loader

  /* ********************************** */

  const pageTransition = {

    in: {
      opacity: 1,
      x: 0,

    },
    out: {
      opacity: 0,
      x: 100,

    }
  };


  /* ********************************** */

  useEffect(() => {
    const timer = setTimeout(() => {
      setactive(false);
      axios.get("/login").then((response) => {
        if (response.data.loggedIn === true) {
          setPhone(response.data.user[0].phone);
          axios.get("/transaction").then((response) => {
            if (response) {
              setHistory(response.data);
            }
          });
        }
        if (
          response.data.loggedIn === false ||
          response.data.user[0].role !== "member"
        ) {
          return history.push("/login");
        }
      });
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div>
      <nav class="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <motion.div initial="out" animate="in" exit="out" variants={pageTransition}>
        <Loader active={active} />
        <div className="history">
          <table className="table table-dark">
            <thead></thead>
            {History.map((val) => {
              return (
                <tbody>
                  <tr>
                    {val.recipient === phone ? (
                      <td key={val.id}>
                        <p className="deposit">ฝาก</p>
                        <p className="deposit-detail">
                          {val.transferor} โอนไปยัง {val.recipient}{" "}
                        </p>
                      </td>
                    ) : (
                      <td key={val.id}>
                        <p className="withdraw">ถอน</p>
                        <p className="withdraw-detail">
                          {val.transferor} โอนไปยัง {val.recipient}{" "}
                        </p>
                      </td>
                    )}

                    {val.transferor === phone ? (
                      <td>
                        <p className="amountminus">- {val.amount}</p>
                        <p className="time">
                          {moment(val.time).format("DD/MMMM - LT")}
                        </p>
                      </td>
                    ) : (
                      <td>
                        <p className="amountplus">+ {val.amount}</p>
                        <p className="time">
                          {moment(val.time).format("DD/MMMM - LT")}
                        </p>
                      </td>
                    )}
                  </tr>
                </tbody>
              );
            })}
          </table>
        </div>
      </motion.div>
    </div>
  );
}
export default Transaction;
