import React, { useState } from 'react';
import { Router } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Buttonshop } from './Buttonshop';
import axios from 'axios';
import { useEffect } from 'react';
import './Menushop.css';


function Menushop() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);


    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false)
        } else {
            setButton(true)
        }
    };

    window.addEventListener('resize', showButton);
    //--------------------------------------------------
    const [point, setPoint] = useState();

    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }
    const refresh = () => {
        axios.get("/shop");
        window.location.replace('/');
    }

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {
                setPoint(response.data.user[0].point);
            }
        });
    }, []);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo">
                        TNC
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

                    </div>


                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>

                        <li className='nav-item'>
                            <Link className='nav-links' onClick={closeMobileMenu}>

                            </Link>
                        </li>

                        {button && <Buttonshop buttonStyle='btn--outline'>MY PROFILE</Buttonshop>}
                        <p onClick={logout} className="navbar-logo">
                            <i className='fas fa-sign-out-alt'></i>
                        </p>
                    </ul>


                </div>

            </nav>

            <div className="main-menu">

                <h1>Point : {point}</h1>
                <button onClick={refresh}>refresh</button>

                <Link to="/transfer-point-shop" className="menu">
                    <i class='fas fa-exchange-alt'></i>
                    โอนคะแนน
                </Link>

                <Link to="/calculate-point-shop" className="menu">
                    <i class='fas fa-square-root-alt'></i>
                    คำนวณคะแนน
                </Link>

                <Link to="/transaction-shop" className="menu">
                    <i class='fas fa-redo-alt'></i>
                    รายการย้อนหลัง
                </Link>

                <Link to="/promotion-shop" className="menu">
                    <i class='fas fa-tag'></i>
                    เพิ่มโปรโมชั่น
                </Link>

                <Link to="/top-up-point-shop" className="menu">
                    <i class='fas fa-money-bill-alt'></i>
                    เติมคะแนน
                </Link>

            </div>

        </>

    );
}
export default Menushop;
