import React from "react";
import { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import './login_and_register.css';
import { Redirect } from "react-router";
import { Link } from "react-router-dom";
import './Login.css';


export default function Login() {



  //----------------------------------------------------------

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [loginStatus, setLoginSatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;

  //login
  const login = () => {
    axios.post("/login", {
      email: email,
      password: password,
    })
      .then((response) => {
        if (response.data.message) {
          setLoginSatus(response.data.message);
        } else {
          window.location.replace('/');
        }
      });
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn == "true") {
    return (
      <Redirect to="/" />
    );
  }
  return (
    <>
      <div className="navbar navbar-default navbar-static-top">
        <div className="container bootstrap snippets bootdey">
          <div className="navbar-header">

            <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i><i className="fas fa-braille" ></i>T N C</Link>

          </div>

        </div>
      </div>


              <label for="user" className="label">Email</label>
              <input className="input" type="text"
                placeholder="Email"
                onChange={(e) => {
                  setEmail(e.target.value);
                }}
                required />

              <label for="pass" className="label">Password</label>
              <input className="input" type="password"
                placeholder="Password"
                onChange={(e) => {
                  setPassword(e.target.value);
                }}
                required />

            <div className="notification">
              <p className="notify">{loginStatus}</p>
            </div>

                <p>ยังไม่ได้เป็นสมาชิค?</p>
                <Link to='/registration'>สมัครสมาชิก</Link><br /><br />
                <input onClick={login} type="submit" className="button" value="LOGIN" />

    </>
  );
}
