import React, { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import { motion } from "framer-motion";
import { Loader } from "semantic-ui-react";
import "./Menumember.css";

function Menumember() {
  //----------------------------------------------------------
  const history = useHistory();
  const [active, setactive] = useState(false); //loader
  const [point, setPoint] = useState();

  const logout = () => {
    setactive(true);
    axios.get("/logout");
    const timer = setTimeout(() => {
      history.push("/home");
    }, 1000);
    return () => clearTimeout(timer);
  };

  const refresh = () => {
    axios.get("/member");
    history.go();
  };

  const profilepage = () => {
    history.push("/memberprofile");
  };

  const transferpage = () => {
    history.push("/transfer-point");
  };

  const transactionpage = () => {
    history.push("/transaction");
  };

  const orderhistory = () => {
    history.push("/order-history");
  };

  const promotionpage = () => {
    history.push("/promotion");
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  const navTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 0,
    },
  };

  /* ********************************** */

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn === true) {
        setPoint(response.data.user[0].point);
      }
      if (
        response.data.loggedIn === false ||
        response.data.user[0].role !== "member"
      ) {
        return history.push("/login");
      }
    });
  }, []);
  return (
    <div className="menumember-page">
      <link
        rel="stylesheet"
        type="text/css"
        href="//netdna.bootstrapcdn.com/font-awesome/4.1.0/css/font-awesome.min.css"
      />
      <link
        href="//maxcdn.bootstrapcdn.com/font-awesome/4.2.0/css/font-awesome.min.css"
        rel="stylesheet"
      ></link>

      {/* <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={navTransition}
     >*/}
      <nav class="navbar navbar-light bg-light justify-content-between">

        <a className="nav-tnc">
          <div className="div-img-logo">
            <img className="img-logoo" src="/images/logo.png" />
          </div>
          T N C
        </a>

        <form class="form-inline">
          <button onClick={logout} className="bn632-hover bn24">
            ออกจากระบบ
          </button>
        </form>
      </nav>
      {/* </motion.div>*/}
      <Loader active={active} />

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <div className="head-frame">
          <div className="data-frame">
            <div className="bg-circle">
              <div className="div-point_point_1_2">
                <p className="point_point_1_2">คะแนนสะสม</p>
              </div>
              <p className="point_1">{point}</p>
              <i onClick={refresh} className="fas fa-sync-alt"></i>
            </div>
            <div className="profile-button">
              <button onClick={profilepage} className="bnmenumember">
                ข้อมูลของฉัน <i className="fas fa-user-edit"></i>
              </button>
            </div>
          </div>
        </div>

        {/*<div className="container bootstrap snippets bootdey">
          <div className="row margin-bottom-10">
            <div className="col-md-4 col-sm-6">
              <div
                onClick={transferpage}
                className="servive-block servive-block-yelloww"
              >
                <img
                  className="imgtransaction1"
                  src="images/transferpoint.png"
                ></img>
                
                <h3 className="heading-md-h2">โอนคะแนน</h3>
              </div>
            </div>
            <div onClick={transactionpage} className="col-md-4 col-sm-6">
              <div className="servive-block servive-block-purplee">
                <img
                  className="imgtransaction1"
                  src="images/transaction.png"
                ></img>
               
                <h3 className="heading-md-h2">ประวัติการโอนคะแนน</h3>
              </div>
            </div>
            <div onClick={orderhistory} className="col-md-4 col-sm-6">
              <div className="servive-block servive-block-aquaa">
                <img
                  className="imgtransaction1"
                  src="images/shophistory.png"
                ></img>
               
                <h3 class="heading-md-h2">ประวัติการซื้อสินค้า</h3>
              </div>
            </div>
            <div onClick={promotionpage} className="col-md-4 col-sm-6">
              <div className="servive-block servive-block-aquaa">
                <img
                  className="imgtransaction1"
                  src="images/promotion.png"
                ></img>
               
                <h3 class="heading-md-h2">โปรโมชัน</h3>
              </div>
            </div>
          </div>
    </div>*/}

<div className="menu1">
        <div className="top-menu">
          <div className="container-menu">
            <div
            onClick={transferpage}
             className="box-menu" 
            >
              <div className="divimg-menu">
                <img className="imgtransaction1" src="images/transferpoint.png" ></img>
              </div>
              <div className="boxmenu">
              <h3 className="heading-md-h2">โอนคะแนน</h3>
              </div>
            </div>
          </div>
        </div>

        <div className="top-menu">
          <div className="container-menu">
            <div
            onClick={transactionpage}
              className="box-menu"   
            >
              <div className="divimg-menu">
                <img className="imgtransaction1" src="images/transaction.png" ></img>
              </div>
              <div className="boxmenu">
              <h3 className="heading-md-h2">ประวัติการโอนคะแนน</h3>
              </div>
            </div>
          </div>
        </div>

        <div className="top-menu">
          <div className="container-menu">
            <div
            onClick={orderhistory}
              className="box-menu" 
            >
              <div className="divimg-menu">
                <img className="imgtransaction1" src="images/shophistory.png" ></img>
              </div>
              <div className="boxmenu">
              <h3 class="heading-md-h2">ประวัติการซื้อสินค้า</h3>
              </div>
            </div>
          </div>
        </div>

        <div className="top-menu">
          <div className="container-menu">
            <div
            onClick={promotionpage}
              className="box-menu"
            >
              <div className="divimg-menu">
                <img className="imgtransaction1" src="images/promotion.png" ></img>
              </div>
              <div className="boxmenu">
              <h3 class="heading-md-h2">โปรโมชัน</h3>
              </div>
            </div>
          </div>
        </div>
        </div>
      </motion.div>
    </div>
  );
}
export default Menumember;
