import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useHistory } from "react-router";
import axios from "axios";
import { useEffect } from "react";
import "./Promotionshop.css";

import Promotiondetails from "./Promotiondetails";

function Promotionshop() {
  let subtitle;
  const [modalIsOpenPromotion, setIsOpenPromotion] = useState(false);
  const [modalIsOpenSelect, setIsOpenSelect] = useState(false);

  function openModalPromotion() {
    setIsOpenPromotion(true);
  }

  function openModalSelect() {
    setIsOpenSelect(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "black";
  }

  function closeModal() {
    setIsOpenPromotion(false);
    setIsOpenSelect(false);
  }
  //-----------------------------------------------
  const history = useHistory();
  const [shoplist, setShoplist] = useState([]);
  const [promotionlist, setPromotionList] = useState([]);
  const [exchange,setexchange] = useState();
  const [shopname,setShopname] = useState("");

  const logout = () => {
    axios.get("/logout");
    window.location.replace("/");
  };

  const getpromotion = (shopname) => {
    axios
      .post("/selectshop", {
        shop_name: shopname,
      })
      .then((response) => {
        //ใส่ modal
        console.log(response.data);
      });

    //window.location.replace('/promotiondetails');
  };

  const selectpromotion = (id) => {  
    axios.post("/selectpromotion", {
      id : id,
    }).then((response) =>{
      if(response) {
        setexchange(response.data[0].amount);
        setShopname(response.data[0].shop_name);
        openModalSelect();
      }
    });
  };

  const gotomyshop = () => {
    history.push("/myshop");
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
      }
      if (
        response.data.loggedIn == false ||
        response.data.user[0].role != "shop"
      ) {
        return window.location.replace("/");
      }
    });

    axios.get("/getshoplistpromotion").then((response) => {
      if (response) {
        setShoplist(response.data);
      }
    });
  }, []);

  return (
    <>
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <div>
        <Modal
          isOpen={modalIsOpenPromotion}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2
            className="h2h2promotion"
            ref={(_subtitle) => (subtitle = _subtitle)}
          >
            รายการ Promotion
          </h2>
          {promotionlist.map((val) => {
            return (
              <div
                className="cotainerpromotion-test"
                onClick={() => {
                  selectpromotion(val.id);
                }}
              >
                <div
                  onClick={() => {
                    selectpromotion(val.id);
                  }}
                  className="boxpromotion"
                >
                  <p>{val.product_name}</p>
                  <p>{val.amount}</p>
                </div>
              </div>
            );
          })}
        </Modal>
      </div>

      <div className="div-transferpointmember">
        <Modal
          isOpen={modalIsOpenSelect}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2
            className="h2transferpointmember"
            ref={(_subtitle) => (subtitle = _subtitle)}
          >
            <i className="fas fa-check-circle"></i>ร้านค้า {shopname} จำนวน{" "}
            {exchange} คะแนน
          </h2>
        </Modal>
      </div>

      <div className="whos-speaking-area speakers pad100">
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              <div className="section-title text-center">
                <div className="title-text mb50">
                  <h2>โปรโมชั่นร้านค้า</h2>
                  <input type="text" placeholder="search" />{" "}
                  <i className="fas fa-search"></i>
                </div>
                <div
                  className="row mb50"
                  onClick={() => {
                    gotomyshop();
                  }}
                >
                  <div className="col-xl-3 col-lg-3 col-md-4 col-sm-12">
                    <div className="speakers xs-mb30">
                      <div className="spk-info">
                        <p>ร้านค้าของฉัน</p>
                      </div>
                    </div>
                  </div>
                </div>
                <hr />
                {shoplist.map((val) => {
                  return (
                    <div
                      className="row mb50"
                      onClick={() => {
                        getpromotion(val.shop_name);
                      }}
                    >
                      <div className="col-xl-3 col-lg-3 col-md-4 col-sm-12">
                        <div className="speakers xs-mb30">
                          <div className="spk-info">
                            <p>{val.shop_name}</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
export default Promotionshop;
