import React from 'react'
import { Link, useHistory } from 'react-router-dom'
import { useEffect } from 'react'
import axios from 'axios'
import { useState } from 'react'
import moment from 'moment'
import { Loader } from 'semantic-ui-react'
import './Transactionshop.css'
import { motion } from 'framer-motion'
import { Accordion, Card } from 'react-bootstrap'

function Transactionshop() {
  const history = useHistory()
  //------------------------------------------------
  axios.defaults.withCredentials = true

  const [History, setHistory] = useState([])
  const [phone, setPhone] = useState('')
  const [name, setname] = useState('')
  const [active, setactive] = useState(true) //loader

  const renderAccordion = (list, index) => {
    return (
      <Accordion key={index}>
        <Card className="or-card">
          <Accordion.Toggle
            as={Card.Header}
            eventKey={list}
            className="accordion-order"
          >
            <div className="div-order">

              {list.transferor === phone ? (
                <div className="td556">
                  <p className="withdraw112">- {list.amount}</p>
                </div>
              ) : (
                <div className="td556">
                  <p className="deposit112">+ {list.amount}</p>
                </div>
              )}

              {list.recipient === phone ? (
                <div key={list.id} className="td55">
                  <p className="deposit1">
                    ฝาก
                  </p>
                  <p className="deposit1-detail">
                    {list.transferor} <i class="fas fa-arrow-right"></i> {list.recipient}
                  </p>
                </div>
              ) : (
                <div key={list.id} className="td55">
                  <p className="withdraw1">
                    ถอน
                  </p>
                  <p className="withdraw1-detail">
                    {list.transferor} <i class="fas fa-arrow-right"></i> {list.recipient}
                  </p>
                </div>
              )}


              <p className="p-orderhistory">
                รายละเอียดเพิ่มเติม <i className="fas fa-arrow-down"></i>
              </p>
            </div>
          </Accordion.Toggle>
          <Accordion.Collapse eventKey={list}>
            <Card.Body>
              <p className='p1234'>รหัสอ้างอิง {list.Tx_id}</p>
              <p className='p1234'>{moment(list.time).format('DD/MMMM - LT')}</p>
            </Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    )
  }
  useEffect(() => {
    const timer = setTimeout(() => {
      setactive(false)
      axios.get('/login').then((response) => {
        if (response.data.loggedIn === true) {
          setname(response.data.user[0].name)
          setPhone(response.data.user[0].phone)
          axios.get('/transaction').then((response) => {
            if (response) {
              setHistory(response.data)
            }
          })
        }
        if (
          response.data.loggedIn === false ||
          response.data.user[0].role !== 'shop'
        ) {
          return history.push('/login')
        }
      })
    }, 1000)
    return () => clearTimeout(timer)
  }, [])

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  }

  /* ********************************** */

  return (
    <div>
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <Loader active={active} />
        <div className="history">
          <div className="div-h1-Orderhistory">
            <h3 className="h1-Orderhistory">ประวัติการโอนคะแนน</h3>
          </div>
          {History.map(renderAccordion)}
          {/* <center>
            <table className="table table-striped">
              <thead></thead>
              {History.map((val) => {
                return (
                  <tbody>
                    <tr className="tr55">
                      <td className="td55">
                        <p className="tx-id1">รหัสอ้างอิง {val.Tx_id}</p>
                      </td>
                      {val.transferor === phone ? (
                        <td key={val.id} className="td55">
                          <p className="withdraw1">
                            ถอน <i class="far fa-frown-open"></i>
                          </p>
                          <p className="withdraw1-detail">
                            {val.transferor} โอนไปยัง {val.recipient}{' '}
                          </p>
                        </td>
                      ) : (
                        <td key={val.id} className="td55">
                          <p className="deposit1">
                            ฝาก <i class="far fa-grin-alt"></i>
                          </p>
                          <p className="deposit1-detail">
                            {val.transferor} โอนไปยัง {val.recipient}{' '}
                          </p>
                        </td>
                      )}

                      {val.transferor === phone ? (
                        <td className="td55">
                          <p className="withdraw1">- {val.amount}</p>
                          <p className="time1">
                            {moment(val.time).format('DD/MMMM - LT')}
                          </p>
                        </td>
                      ) : (
                        <td className="td55">
                          <p className="deposit1">+ {val.amount}</p>
                          <p className="time1">
                            {moment(val.time).format('DD/MMMM - LT')}
                          </p>
                        </td>
                      )}
                    </tr>
                  </tbody>
                )
              })}
            </table>
          </center> */}
        </div>
      </motion.div>
    </div>
  )
}
export default Transactionshop
