import React from "react";
import { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import './login_and_register.css';
import { Link } from "react-router-dom";
import { Redirect } from "react-router";
import './Registeration.css';


export default function Registeration() {
 
    


  //----------------------------------------------------------
  const [nameReg, setNameReg] = useState("");
  const [phoneReg, setPhoneReg] = useState("");
  const [emailReg, setEmailReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  const [typeReg, setTypeReg] = useState("");

  const [memberregisterStatus, setmemberRegisterStatus] = useState("");
  const [shopregisterStatus, setshopRegisterStatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;
  //register
  const memberregister = () => {
    axios
      .post("/memberregister", {
        name: nameReg,
        phone: phoneReg,
        email: emailReg,
        password: passwordReg,
      })
      .then((response) => {
        alert(response.data.message);
        setmemberRegisterStatus(response.data.message);
      });
  };
  const shopregister = () => {
    axios
      .post("/shopregister", {
        name: nameReg,
        phone: phoneReg,
        email: emailReg,
        password: passwordReg,
        type: typeReg
      })
      .then((response) => {
        alert(response.data.message);
        setshopRegisterStatus(response.data.message);
      });
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn == "true") {
    return (
      <Redirect to="/" />
    );
  }


  return (
    <div >
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i></Link>
      </nav>
      



      <div className="register-page">
        <div className="register-con">
          <div className="row-register">
            <div className="button-register">
              <div id="buttonregis"></div>
              <button  type='button ' className="button-member">ลูกค้า</button>
              <button  type='button ' className="button-shop">ร้านค้า</button>
            </div>

            <div className="account-register">
              <from id="memberregisterr" className="form-register" onSubmit={memberregister}>
                <div className="member-register">
                  <div className="group-member-register-name">

                    <input className="member-register-name" type="text"
                      placeholder="Name"
                      onChange={(e) => {
                        setNameReg(e.target.value);
                      }}
                      required />
                  </div>
                  <div className="group-member-register-phone">

                    <input className="member-register-name" type="text"
                      placeholder="Phone"
                      onChange={(e) => {
                        setPhoneReg(e.target.value);
                      }}
                      required />
                  </div>
                  <div className="group-member-register-email">

                    <input className="member-register-name" type="email"
                      placeholder="Email"
                      onChange={(e) => {
                        setEmailReg(e.target.value);
                      }}
                      required />
                  </div>
                  <div className="group-member-register-password">

                    <input className="member-register-name" type="password"
                      placeholder="Password"
                      onChange={(e) => {
                        setPasswordReg(e.target.value);
                      }}
                      required />
                  </div>
                  <button className="bn29" type="submit">สมัครสมาชิก</button>
                </div>
              </from>

              <from id="shopregisterr" className="form-register" onSubmit={shopregister}>
                <div className="shop-register">
                  <div className="group-shop-register-name">

                    <input className="shop-register-name" type="text"
                      placeholder="Name"
                      onChange={(e) => {
                        setNameReg(e.target.value);
                      }}
                      required />
                  </div>
                  <div className="group-shop-register-phone">

                    <input className="shop-register-name" type="text"
                      placeholder="Phone"
                      onChange={(e) => {
                        setPhoneReg(e.target.value);
                      }}
                      required />
                  </div>
                  <div className="group-shop-register-email">

                    <input className="shop-register-name" type="email"
                      placeholder="Email"
                      onChange={(e) => {
                        setEmailReg(e.target.value);
                      }}
                      required />
                  </div>
                  <div className="group-shop-register-password">

                    <input className="shop-register-name" type="password"
                      placeholder="Password"
                      onChange={(e) => {
                        setPasswordReg(e.target.value);
                      }}
                      required />
                  </div>
                  <div className="group-shop-register-shop-type">

                    <input className="shop-register-name" type="text"
                      placeholder="Shop Type"
                      onChange={(e) => {
                        setTypeReg(e.target.value);
                      }}
                      required />
                  </div>

                  <button className="bn29" type="submit">สมัครสมาชิก</button>
                </div>
              </from>
            

            </div>
          </div>
        </div>
                    </div>
      
     
     


