import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { useHistory } from "react-router";
import { motion } from "framer-motion";

import "./Changepwd.css";

function Changepwd(emailreq) {
  const history = useHistory();
  const [email, setemail] = useState("");
  const [Newpwd, setNewpwd] = useState("");
  const [ConfirmNewpwd, setConfirmNewpwd] = useState("");

  const checkPassword = (e) => {
    e.preventDefault();
    if (!Newpwd || !ConfirmNewpwd) {
      alert("กรุณาป้อนรหัสผ่านให้ครบ");
    } else {
      if (Newpwd === ConfirmNewpwd) {
        changepassword();
      } else {
        alert("รหัสผ่านไม่ตรงกัน");
      }
    }
  };
  const changepassword = () => {
    axios
      .put("/changepassword", { email: email, password: ConfirmNewpwd })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message);
          history.push("/login");
        } else if (response.data.err) {
          alert(response.data.err);
        }
      });
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */

  useEffect(() => {
    setemail(emailreq.match.params.email);
  }, []);

  return (
    <motion.div initial="out" animate="in" exit="out" variants={pageTransition}>
      <form onSubmit={checkPassword}>
        <h1 className="changepwd-email">Email : {email}</h1>
        <label className="changepwd-new">รหัสผ่านใหม่</label>
        <input
          type="password"
          className="input-changepwd-new"
          placeholder="รหัสผ่านใหม่"
          onChange={(e) => {
            setNewpwd(e.target.value);
          }}
        />
        <label className="changepwd-new">ยืนยันรหัสผ่านใหม่</label>
        <input
          type="password"
          className="input-changepwd-new"
          placeholder="ยืนยันรหัสผ่านใหม่"
          onChange={(e) => {
            setConfirmNewpwd(e.target.value);
          }}
        />
        <button className="btn btn-success changepwd-btn">ยืนยัน</button>
      </form>
    </motion.div>
  );
}

export default Changepwd;
