import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useEffect } from "react";
import axios from "axios";
import "./Calculatepointshop.css";

function Calculatepointshop() {
  const history = useHistory();
  //-----------------------------------------------------

  const [id, setId] = useState();
  const [shoppoint, setShopPoint] = useState();
  const [phone, setPhone] = useState("");
  const [destPhone, setdestPhone] = useState("");
  const [amount, setamount] = useState();
  const [transferStatus, setTransferStatus] = useState("");

  const calPoint = () => {
    if (destPhone == "") {
      setTransferStatus("เบอร์ปลายทางไม่ถูกต้อง");
    } else if (phone == destPhone) {
      setTransferStatus("เบอร์ปลายทางไม่ถูกต้อง");
    } else if (amount > shoppoint) {
      setTransferStatus("คะแนนไม่เพียงพอสำหรับการโอน กรุณาเติมคะแนน");
    } else if (amount < 1) {
      setTransferStatus("ยอดซื้อขั้นต่ำ 50-.");
    } else {
      axios
        .put("/calPointTransfer", {
          id: id,
          phone: phone,
          destphone: destPhone,
          amount: amount,
        })
        .then((response) => {
          setTransferStatus(response.data.message);
          if (response.data.loggedIn == true) {
            setShopPoint(response.data.user[0].shoppoint);
            window.location.reload();
          }
        });
    }
  };

  const logout = () => {
    axios.get("/logout");
    window.location.replace("/");
  };

  const gotoTopupPage = () => {
    history.push("/top-up-point-shop");
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setId(response.data.user[0].id);
        setShopPoint(response.data.user[0].shoppoint);
        setPhone(response.data.user[0].phone);
      }
      if (
        response.data.loggedIn == false ||
        response.data.user[0].role != "shop"
      ) {
        return window.location.replace("/");
      }
    });
  }, []);

  return (
    <>
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <div className="calculate">
        <button onClick={gotoTopupPage} className="buttontop-up">
          <i class="fas fa-plus"></i>
          เติมคะแนน
        </button>

        <div className="calculate-con">
          <h2 className="h2promotion" >คำนวณคะแนน</h2>
          <h1 className="h1promotion">คะแนนคงเหลือ : {shoppoint}</h1>
          <div className="textbox-cal">
            <i className="fas fa-phone-square-alt"></i> <label className="labelp">กรอกเบอร์โทรศัพท์</label>
            <input className="inputcalculate" placeholder= "กรอกเบอร์โทรศัพท์" 
              type="text" 
              onChange={(e) => {
                setdestPhone(e.target.value);
              }} 
            />
          </div>
          <div className="textbox-cal">
           <i className="fas fa-dollar-sign"></i> <label className="labelp">กรอกราคาสินค้า</label>
            <input className="inputcalculate" placeholder="กรอกราคาสินค้า"  
              type="number" 
              onChange={(e) => { 
                setamount(Math.floor(e.target.value / 50));
              }}
               />
            
          </div>
          {transferStatus}
            <button className="button-cal" onClick={calPoint}>ยืนยัน</button>
            
        </div>
      </div>

    </>
  );
}
export default Calculatepointshop;
